SELECT a1.machine_id, ROUND(AVG(a2.timestamp - a1.timestamp), 3) as processing_time FROM (SELECT * FROM Activity WHERE activity_type = 'start' ORDER BY process_id) a1 INNER JOIN (SELECT * FROM Activity WHERE activity_type = 'end' ORDER BY process_id) a2 ON a1.machine_id = a2.machine_id AND a1.process_id = a2.process_id GROUP BY a1.machine_id;